import logo from './logo.svg';
import './App.css';

function App() {
  return (
    <div>
      <form onSubmit={mostrar}>
          <input type="text" name="txt" />
        <p>
          <input type="submit" value="enviar" />
        </p>
      </form>
    </div>
  );
  function mostrar(e)
  {
    e.preventDefault();
    const textoEnviado = e.target.txt.value
    alert(textoEnviado);
  }

}

export default App;
